Configuration.ScriptBody:
$culture = [System.Globalization.CultureInfo] 'en-US' [System.Threading.Thread]::CurrentThread.CurrentUICulture = $culture [System.Threading.Thread]::CurrentThread.CurrentCulture = $culture
$Script:API = new-object -comObject "MOM.ScriptAPI" $Script:Bag = $Script:API.CreatePropertyBag()
$warning = $false $store=new-object System.Security.Cryptography.X509Certificates.X509Store("My","LocalMachine") $store.open("ReadOnly") ForEach($cert in $store.certificates) { If($cert.NotAfter.subtract([datetime]::now).days -lt 7) { $Script:API.LogScriptEvent("NPS-CertificateMonitor.ps1", 0, 0, "Found expired certificate") $Script:Bag.AddValue("Status","Critical") $Script:Bag.AddValue("Certificate Expires", $cert.NotAfter) $Script:Bag.AddValue("Certificate Subject", $cert.subject) $Script:API.Return($Bag) #$Script:Bag exit }
If($cert.NotAfter.subtract([datetime]::now).days -lt 21) { $Script:API.LogScriptEvent("NPS-CertificateMonitor.ps1", 0, 0, "Found soon to expire certificate") $warning = $true $WarningCert = $cert } } If ($warning) { $Script:Bag.AddValue("Status","Warning") $Script:Bag.AddValue("Certificate Expires", $cert.NotAfter) $Script:Bag.AddValue("Certificate Subject", $WarningCert.subject) $Script:API.Return($Bag) #$Script:Bag } Else { $Script:API.LogScriptEvent("NPS-CertificateMonitor.ps1", 0, 0, "All certificates are up to date") $Script:Bag.AddValue("Status","Healthy") $Script:Bag.AddValue("Certificate Expires", $cert.NotAfter) $Script:Bag.AddValue("Certificate Subject", $cert.subject) $Script:API.Return($Bag) #$Script:Bag }

